import React from 'react';
 import { Formik, Form, Field, FieldArray } from 'formik';

 // Here is an example of a form with an editable list.
 // Next to each input are buttons for insert and remove.
 // If the list is empty, there is a button to add an item.
 export const FriendList = ({data}) => (
   <div>
     <h1>Friend List</h1>
     <Formik
    initialValues={{email:"",domain:""}}
    // ...and do some more stuff here like error handling submit handling etc...
    render={({ values, handleSubmit, handleChange, handleBlur }) => (
      <Form onSubmit={handleSubmit}>
        <Field
          name="domain"
          value={values.domain}
          render={({ field, form }) => (
            <input
              {...field}
              onChange={e => {
                const domain = e.target.value.replace(/.*@/, '')
                handleChange(e)
                form.setFieldValue('mail.domain', domain)
              }}
            />
          )}
        />
        <input
          name="email"
          value={values.email}
          onChange={handleChange}
          onBlur={handleBlur}
        />
       <button type="submit">Submit</button>
      </Form>
    )}
  />
   </div>
 );


 export default FriendList
